import { render } from "@testing-library/react";
import { translate } from "../utils/translate";
export default class Todo extends React.Component{
    constructor(props){
        super();
        this.state = {
            text: props.text,
        }
    }
    componentDidMount(){
        let translated = ""
    translate(text, { to: "fr" }).then(res=>translated= res).catch(err=>console.error(err))
    console.log(translated)
    }

    render(){
        return (
            <li >
               <input type="checkbox" />
               <span>{translated}</span>
            </li>
           )
    }

}
